import React, { useState } from 'react';

// This is the main component for our application
function App() {
    // --- State Management ---
    // These variables will hold the data for our component
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegister, setIsRegister] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [token, setToken] = useState(null);

    // State for the chat
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    // --- Handlers for Authentication ---
    const handleAuth = async (e) => {
        e.preventDefault();
        const endpoint = isRegister ? '/register' : '/login';
        setError(''); // Clear previous errors

        try {
            const response = await fetch(`http://localhost:5000/api/auth${endpoint}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, password })
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.msg || 'Authentication failed');
            }

            setToken(data.token);
            setIsLoggedIn(true);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setToken(null);
        setChatHistory([]);
    };

    // --- Handler for Sending Chat Messages ---
    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!message.trim()) return;

        const newHistory = [...chatHistory, { sender: 'user', text: message }];
        setChatHistory(newHistory);
        setMessage('');
        setIsLoading(true);

        try {
            const response = await fetch('http://localhost:5000/api/chat', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ message })
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error('Failed to get response from AI');
            }
            
            // Add the AI's reply to the chat history
            setChatHistory([...newHistory, { sender: 'ai', text: data.reply }]);

        } catch (err) {
             setChatHistory([...newHistory, { sender: 'ai', text: `Error: ${err.message}` }]);
        } finally {
            setIsLoading(false);
        }
    };


    // --- Render Logic ---
    // This returns the HTML that will be displayed
    return (
        <div className="bg-gray-100 min-h-screen flex items-center justify-center font-sans">
            <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-8">
                
                {/* If the user is logged in, show the chat view */}
                {isLoggedIn ? (
                    <div>
                        <div className="flex justify-between items-center mb-4">
                           <h1 className="text-2xl font-bold text-red-600">AI First Aid Chat</h1>
                           <button onClick={handleLogout} className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-300">Logout</button>
                        </div>

                        {/* Safety Disclaimer */}
                        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4 rounded-md">
                            <p className="font-bold">Important Safety Notice</p>
                            <p>This is AI-generated advice and not a substitute for a real doctor. In a serious emergency, call your local emergency services immediately.</p>
                        </div>
                       
                        {/* Chat History */}
                        <div className="h-80 overflow-y-auto mb-4 p-4 bg-gray-50 rounded-md border">
                            {chatHistory.map((chat, index) => (
                                <div key={index} className={`mb-4 ${chat.sender === 'user' ? 'text-right' : 'text-left'}`}>
                                    <div className={`inline-block p-3 rounded-lg ${chat.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}>
                                        <p style={{ whiteSpace: 'pre-wrap' }}>{chat.text}</p>
                                    </div>
                                </div>
                            ))}
                            {isLoading && <p className="text-center text-gray-500">AI is typing...</p>}
                        </div>

                        {/* Message Input Form */}
                        <form onSubmit={handleSendMessage}>
                            <div className="flex">
                                <input
                                    type="text"
                                    value={message}
                                    onChange={(e) => setMessage(e.target.value)}
                                    placeholder="Ask a first-aid question..."
                                    className="flex-grow p-3 border rounded-l-md focus:outline-none focus:ring-2 focus:ring-red-500"
                                    disabled={isLoading}
                                />
                                <button type="submit" className="bg-red-500 text-white px-6 py-3 rounded-r-md hover:bg-red-600 disabled:bg-red-300" disabled={isLoading}>
                                    Send
                                </button>
                            </div>
                        </form>
                    </div>

                /* If the user is NOT logged in, show the auth form */
                ) : (
                    <div>
                         <h1 className="text-3xl font-bold text-center mb-2 text-red-600">AI First Aid Assistant</h1>
                        <p className="text-center text-gray-600 mb-6">{isRegister ? 'Create an account' : 'Welcome back'}</p>
                        <form onSubmit={handleAuth}>
                            <div className="mb-4">
                                <label className="block text-gray-700 mb-2">Email</label>
                                <input
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500"
                                    required
                                />
                            </div>
                            <div className="mb-6">
                                <label className="block text-gray-700 mb-2">Password</label>
                                <input
                                    type="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500"
                                    required
                                />
                            </div>
                            {error && <p className="text-red-500 text-center mb-4">{error}</p>}
                            <button type="submit" className="w-full bg-red-500 text-white p-3 rounded-md hover:bg-red-600">
                                {isRegister ? 'Register' : 'Login'}
                            </button>
                            <p className="text-center mt-4">
                                {isRegister ? 'Already have an account?' : "Don't have an account?"}
                                <button type="button" onClick={() => setIsRegister(!isRegister)} className="text-red-500 hover:underline ml-1">
                                    {isRegister ? 'Login' : 'Register'}
                                </button>
                            </p>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
}

// This line is the fix. It makes the App component available for import.
export default App;
