import React, { useState } from 'react';

// This is the main component for our application
function App() {
    // --- State Management ---
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegister, setIsRegister] = useState(true); // Default to register view to show new fields
    const [error, setError] = useState('');
    const [token, setToken] = useState(null);

    // State for the auth form fields
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        name: '',
        age: '',
        bloodGroup: 'A+', // Default value
        medicalInfo: '',
        medicalHistory: ''
    });

    // State for the chat
    const [message, setMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    // --- Handlers for Authentication ---
    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleAuth = async (e) => {
        e.preventDefault();
        const endpoint = isRegister ? '/register' : '/login';
        setError(''); // Clear previous errors

        // Prepare the data to be sent to the backend
        const body = isRegister ? formData : { email: formData.email, password: formData.password };

        try {
            const response = await fetch(`http://localhost:5000/api/auth${endpoint}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(body)
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.msg || 'Authentication failed');
            }

            setToken(data.token);
            setIsLoggedIn(true);
        } catch (err) {
            setError(err.message);
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setToken(null);
        setChatHistory([]);
    };

    // --- Handler for Sending Chat Messages ---
    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!message.trim()) return;

        const newHistory = [...chatHistory, { sender: 'user', text: message }];
        setChatHistory(newHistory);
        setMessage('');
        setIsLoading(true);

        try {
            const response = await fetch('http://localhost:5000/api/chat', {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json',
                    // We will add the token here later for personalized AI responses
                },
                body: JSON.stringify({ message })
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error('Failed to get response from AI');
            }
            
            setChatHistory([...newHistory, { sender: 'ai', text: data.reply }]);

        } catch (err) {
             setChatHistory([...newHistory, { sender: 'ai', text: `Error: ${err.message}` }]);
        } finally {
            setIsLoading(false);
        }
    };


    // --- Render Logic ---
    return (
        <div className="bg-gray-100 min-h-screen flex items-center justify-center font-sans py-12">
            <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-8">
                
                {isLoggedIn ? (
                    // --- CHAT VIEW ---
                    <div>
                        <div className="flex justify-between items-center mb-4">
                           <h1 className="text-2xl font-bold text-red-600">AI First Aid Chat</h1>
                           <button onClick={handleLogout} className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-300">Logout</button>
                        </div>
                        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4 rounded-md">
                            <p className="font-bold">Important Safety Notice</p>
                            <p>This is AI-generated advice and not a substitute for a real doctor. In a serious emergency, call your local emergency services immediately.</p>
                        </div>
                        <div className="h-80 overflow-y-auto mb-4 p-4 bg-gray-50 rounded-md border">
                            {chatHistory.map((chat, index) => (
                                <div key={index} className={`mb-4 ${chat.sender === 'user' ? 'text-right' : 'text-left'}`}>
                                    <div className={`inline-block p-3 rounded-lg ${chat.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}>
                                        <p style={{ whiteSpace: 'pre-wrap' }}>{chat.text}</p>
                                    </div>
                                </div>
                            ))}
                            {isLoading && <p className="text-center text-gray-500">AI is typing...</p>}
                        </div>
                        <form onSubmit={handleSendMessage}>
                            <div className="flex">
                                <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Ask a first-aid question..." className="flex-grow p-3 border rounded-l-md focus:outline-none focus:ring-2 focus:ring-red-500" disabled={isLoading}/>
                                <button type="submit" className="bg-red-500 text-white px-6 py-3 rounded-r-md hover:bg-red-600 disabled:bg-red-300" disabled={isLoading}>Send</button>
                            </div>
                        </form>
                    </div>
                ) : (
                    // --- AUTH VIEW ---
                    <div>
                         <h1 className="text-3xl font-bold text-center mb-2 text-red-600">AI First Aid Assistant</h1>
                        <p className="text-center text-gray-600 mb-6">{isRegister ? 'Create Your Medical Profile' : 'Welcome back'}</p>
                        <form onSubmit={handleAuth} className="space-y-4">
                            
                            {isRegister && (
                                <>
                                    <input name="name" type="text" value={formData.name} onChange={handleInputChange} placeholder="Full Name" className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500" required />
                                    <input name="age" type="number" value={formData.age} onChange={handleInputChange} placeholder="Age" className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500" required />
                                    <div>
                                        <label className="block text-gray-700 mb-1 text-sm">Blood Group</label>
                                        <select name="bloodGroup" value={formData.bloodGroup} onChange={handleInputChange} className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500">
                                            {['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'].map(bg => <option key={bg} value={bg}>{bg}</option>)}
                                        </select>
                                    </div>
                                </>
                            )}

                            <input name="email" type="email" value={formData.email} onChange={handleInputChange} placeholder="Email Address" className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500" required />
                            <input name="password" type="password" value={formData.password} onChange={handleInputChange} placeholder="Password" className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500" required />

                             {isRegister && (
                                <>
                                    <textarea name="medicalInfo" value={formData.medicalInfo} onChange={handleInputChange} placeholder="Current Allergies / Medical Conditions (e.g., Penicillin allergy, Asthma)" className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500" rows="3"></textarea>
                                    <textarea name="medicalHistory" value={formData.medicalHistory} onChange={handleInputChange} placeholder="Past Medical History (e.g., Appendix surgery 2018)" className="w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-red-500" rows="3"></textarea>
                                </>
                            )}

                            {error && <p className="text-red-500 text-center">{error}</p>}
                            <button type="submit" className="w-full bg-red-500 text-white p-3 rounded-md hover:bg-red-600">{isRegister ? 'Register' : 'Login'}</button>
                            
                            <p className="text-center pt-2">
                                {isRegister ? 'Already have an account?' : "Don't have an account?"}
                                <button type="button" onClick={() => setIsRegister(!isRegister)} className="text-red-500 hover:underline ml-1">
                                    {isRegister ? 'Login' : 'Register'}
                                </button>
                            </p>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
